        Graph.prototype.stencilHasPlaceholders = function(stencil)
        {
            if (stencil != null && stencil.fgNode != null)
            {
                var node = stencil.fgNode.firstChild;
                while (node != null)
                {
                    if (node.nodeName == 'text' && node.getAttribute('placeholders') == '1')
                        return true;
                    node = node.nextSibling;
                }
            }
            return false;
        };
        
